import React from "https://cdn.jsdelivr.net/npm/react@18.2.0/+esm";
import ReactDOM from "https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm";

function TaskCard() {
  // Get count from appsmith.model, fallback to 145
  const [count, setCount] = React.useState(appsmith.model.count ?? 145);

  React.useEffect(() => {
    // Listen to model changes from Appsmith (dynamic updates)
    appsmith.onModelChange((model, prevModel) => {
      if (model.count !== prevModel?.count) {
        setCount(model.count ?? 145);
      }
    });
  }, []);

  return (
    <div className="task-container-dashboard">
      <div className="task-card">
        <h1>{count}</h1>
        <p>Estimated Hours for In-Progress Tasks</p>
      </div>
    </div>
  );
}

appsmith.onReady(() => {
  ReactDOM.render(<TaskCard />, document.getElementById("root"));
});
